if (linkStartIndex >= 0)
{
    var levelStartIndex = info.indexOf('Signal level=');
    var linkStopIndex   = info.indexOf('/70');
    linkQuality = Number(info.substring(linkStartIndex + 13, linkStopIndex));
    signalLevel =  Number(info.substring(levelStartIndex + 13, levelStartIndex + 17));
    ssid = info.substring(info.indexOf('ESSID') + 7);
    ssid = ssid.substring(0,ssid.indexOf('"'));
}
var val = {linkQuality : linkQuality, signalLevel: signalLevel, ssid:ssid};
global.set('linkQuality',linkQuality);
global.set('signalLevel', signalLevel);
global.set('ssid', ssid);
