{
    var errCount = context.get('errCount');
    if (errCount == undefined) errCount = 0;
    ++errCount
    if (errCount < 3)
    {
        context.set('errCount',errCount);
        return null;
    }
    else
    {
        context.set('errCount',0);
        node.warn("Bad Image three times in a row");
        return null;
    }
}
else
