var imgArray = [];
var dataView     = new DataView(new ArrayBuffer(2));
var ndataPts = 4800 * device.resMode.value * device.resMode.value;
var nrows = 60 * device.resMode.value;
var ncols = 80 * device.resMode.value;
for (var irow = 0; irow < ndataPts; ++irow)
{
        dataView.setUint8(0, msg.payload[irow * 2]);
        dataView.setUint8(1, msg.payload[irow * 2 + 1]);
        imgArray[irow] = dataView.getUint16(0);
}
var minval = imgArray[0];
var maxval = imgArray[0];
for (var irow = 0; irow < ndataPts; ++irow)
{
        if ( minval > imgArray[irow]) minval = imgArray[irow];
        if ( maxval < imgArray[irow]) maxval = imgArray[irow];
}
if (minval == 0) return null;

var deltatTcal = (new Date().getTime() - flow.get('calTime')) / 1000.0;
var tCorr = 0.0;
var trise = 4.0;
var tdecay = 12.0;
if ((0.0 < deltatTcal) &&(deltatTcal < 60.0))
{
    tCorr = Math.round(-100.0 * (deltatTcal / trise) * Math.exp(-deltatTcal / tdecay));
    for (var irow = 0; irow < ndataPts; ++irow)
    {
        imgArray[irow] = imgArray[irow] + tCorr;
    }
    minval = minval + tCorr;
    maxval = maxval + tCorr;
}

var imgArray2D = [];
var deltaVal = maxval - minval;
if (deltaVal == 0) deltaVal = 1;
var numPix = 0;
var tempThreshold = device.archiveMinTemp.value * 100 + 27315;
for (var irow = 0; irow < nrows; ++irow)
{
    imgArray2D[irow] = [];
    for (var icol = 0; icol < ncols; ++icol)
    {
        imgArray2D[irow][icol] = Math.floor(256.0 * (imgArray[irow * ncols + icol] - minval) / deltaVal);
        if (imgArray[irow * ncols + icol] >= tempThreshold) ++numPix;
    }    
}
var imgArray2DT = [];
if (device.transpose.value == 1)
{
    for (var icol = 0; icol < ncols; ++icol)
    {
        imgArray2DT[icol] = [];
        for (var irow = 0; irow < nrows; ++irow)
        {
            imgArray2DT[icol][irow] = imgArray2D[irow][icol];
        }
    }
    imgArray2D = imgArray2DT;
    var tempNrows = nrows;
    nrows = ncols;
    ncols = tempNrows; 
}
var imgArray2DFV = [];
if (device.flipVertical.value == 1)
{
    for (var irow = 0; irow < nrows; ++irow)
    {
        imgArray2DFV[irow] = [];
        for (var icol = 0; icol < ncols; ++icol)
        {
            imgArray2DFV[irow][icol] = imgArray2D[nrows - irow - 1][icol];
        }    
    }
    imgArray2D = imgArray2DFV;
}
var imgArray2DFH = [];
if (device.flipHorizontal.value == 1)
{
    for (var irow = 0; irow < nrows; ++irow)
    {
        imgArray2DFH[irow] = [];
        for (var icol = 0; icol < ncols; ++icol)
        {
            imgArray2DFH[irow][icol] = imgArray2D[irow][ncols - icol - 1];
        }    
    }
    imgArray2D = imgArray2DFH;
}
var pgm = 
{
    'type'      : 'P2',
    'timeStamp' : new Date().getTime(),
    'nrows'     : nrows,
    'ncols'     : ncols,
    'minval'    : minval,
    'maxval'    : maxval,
    'imageArray': imgArray2D
};
if (numPix > 10)
{
    if (device.autoModeAction.value == 1)
    {
        var autoModeAction1Count = context.get('autoModeAction1Count') + 1;
        if (autoModeAction1Count > 3)
        {
            autoModeAction1Count = 0;
            device.autoModeAction.value = 2;
        }
        context.set('autoModeAction1Count',autoModeAction1Count);
    }
}
device.imageData.value = pgm;
device.pixelMin.value = Math.round(0.1 *  (pgm.minval - 27315) ) / 10;
device.pixelMax.value  = Math.round(0.1 *  (pgm.maxval - 27315) ) / 10;
device.bodyTemp.value = device.pixelMax.value + device.offsetTemp.value; 
//node.warn(device.bodyTemp.value);
device.timeStamp = pgm.timeStamp;
var toppy = global.get('project') + '/' + global.get('deviceTypeA') + '/' + global.get('deviceNameA') + '/reading';

return {topic:toppy,payload:device};

