var process = flow.get('cameraProcess');
if (process == undefined) return null;
var oldProcess = context.get('pidProcess');
var killMsg = null;
if (process.pid != undefined)
{
    var pid = Number(process.pid.substring(4)) + 1;
    if (oldProcess.process < 0)
    {
        oldProcess.process = pid;
        oldProcess.time = new Date().getTime();
    }
    else
    {
        if (oldProcess.process != pid)
        {
            oldProcess.process = pid;
            oldProcess.time = new Date().getTime();
        }
        else
        {
            node.warn('Stale process ' + pid.toString());
            if ((new Date().getTime() - oldProcess.time) > 5000)
            {
                killMsg = {topic:'killProcess',payload:pid};
                node.warn('Killing process ' + pid.toString());
                oldProcess.process = -1;
                oldProcess.time = 0;
            }
        }
    }
}
else
{
    oldProcess.process = -1;
    oldProcess.time = 0;
}
context.set('pidProcess',oldProcess);
return killMsg;

