        function updateDmaReadings(device)
        {
            if (device.imageData.value == null) return;
            var tempBarConfig = 
            {
                lolo            : device.bodyTemp.alarm.limits.lolo, 
                low             : device.bodyTemp.alarm.limits.low, 
                high            : device.bodyTemp.alarm.limits.high, 
                hihi            : device.bodyTemp.alarm.limits.hihi, 
                value           : device.bodyTemp.value, 
                unit            : device.bodyTemp.unit, 
                title           : '',
                textsize        : 20,
                label           : 'temp', 
                lowColor        : '#0000ff', 
                mediumColor     : '#00ff00',
                highColor       : '#ff0000', 
                backgroundColor : '#000000', 
                labelColor      : '#ffffff', 
                gridColor       : '#000000',
                titleColor      : '#000000',
                height          : 125, 
                width           : 440,
                htmlId          : 'tempBarPlot',
                scale           : (displayWindowWidth / 480)
            };
            $('#imageId').show();
            $('#logoId').hide();

            var style = '';
            if (device.autoModeAction.value == 1)
            {
                $('#instructionId').show();
                $('#tempBarId').hide();
                $('#instruction1Id').text(instructions[device.language.value][0]);
                $('#instruction2Id').text(instructions[device.language.value][1]);
                $('#instruction3Id').text(instructions[device.language.value][2]);
            }
            if (device.autoModeAction.value == 2)
            {
                $('#instructionId').hide();
                $('#tempBarId').show();
                $('#tempComment').css("color", "#ff00ff");
                $('#tempComment').css("background-color", "#000000");
                $('#tempComment').text(warnings[0][device.language.value]);
                tempBarConfig.lowColor = '#000000';
                tempBarConfig.mediumColor = '#000000';
                tempBarConfig.highColor = '#000000';
                tempBarConfig.labelColor = '#000000';
                tempBarConfig.titleColor = '#000000';
            }
            if (device.autoModeAction.value == 3)
            {
                $('#instructionId').hide();
                $('#tempBarId').show();
                $('#tempComment').css("color", "#ff00ff");
                $('#tempComment').css("background-color", "#000000");
                $('#tempComment').text("Measuring...");
                if (device.bodyTemp.value < device.bodyTemp.alarm.limits.low)
                {
                    tempBarConfig.titleColor = '#0000ff';
                }
                else
                {
                    if (device.bodyTemp.value < device.bodyTemp.alarm.limits.high)
                    {
                        tempBarConfig.titleColor = '#00ff00';
                    }
                    else
                    {
                        tempBarConfig.titleColor = '#ff0000';
                    }
                }
            }
            if (device.autoModeAction.value == 4)
            {
                $('#instructionId').hide();
                $('#tempBarId').show();
                $('#tempBarPlot').show();
                if ((device.bodyTemp.alarm.limits.lolo <= device.bodyTemp.value) && (device.bodyTemp.value <= device.bodyTemp.alarm.limits.hihi))
                {
                    var infoText = aquireDataInfo[device.language.value];
                    if (device.bodyTemp.value < device.bodyTemp.alarm.limits.low)
                    {
                        tempBarConfig.titleColor = '#0000ff';
                        style = '#0000ff';
                        infoText = warnings[1][device.language.value];
                        colorScale = true;
                    }
                    else
                    {
                        if (device.bodyTemp.value < device.bodyTemp.alarm.limits.high)
                        {
                            tempBarConfig.titleColor = '#00ff00';
                            style = '#00ff00';
                            infoText = warnings[1][device.language.value];
                            colorScale = true;
                        }
                        else
                        {
                            tempBarConfig.titleColor = '#ff0000';
                            style = '#ff0000';
