        function plotImage(device)
        {
            var rgb = [];
            var plow = (pgm.minval - 27315) / 100;
            var phigh = (pgm.maxval - 27315) / 100;
            var pdelta = phigh - plow;
            var canvas = document.getElementById("flirCanvas");
            var canvasContext = canvas.getContext("2d");

            var canvasWidth  = Number($('#flirCanvas').attr('width'));
            var canvasHeight = Number($('#flirCanvas').attr('height'));
            var rectWidth  = canvasWidth / pgm.ncols;
            var rectHeight =  canvasHeight / pgm.nrows;

            for (var irow = 0; irow < pgm.nrows; ++irow)
            {
                for (var icol = 0; icol < pgm.ncols; ++icol)
                {
                    var intensity = imageArray[irow][icol] / 256.0;
                    if (enhancementLevel == -2) intensity = Math.pow(intensity, 1/3.0);
                    if (enhancementLevel == -1) intensity = Math.pow(intensity, 1/2.0);
                    if (enhancementLevel ==  0) intensity = Math.pow(intensity, 1);
                    if (enhancementLevel ==  1) intensity = Math.pow(intensity, 2.0);
                    if (enhancementLevel ==  2) intensity = Math.pow(intensity, 3.0);
                    if (colorScale)
                    {
                        rgb = getRGB(intensity);
                        rgb[0] = Math.floor(rgb[0]);
                        rgb[1] = Math.floor(rgb[1]);
                        rgb[2] = Math.floor(rgb[2]);
                    }
                    else
                    {
                        rgb[0] = Math.floor(255 * intensity);
                        rgb[1] = Math.floor(255 * intensity);
                        rgb[2] = Math.floor(255 * intensity);
                    }
                    canvasContext.fillStyle = 'rgb(' + rgb[0].toString() + ', ' + rgb[1].toString() + ', ' + rgb[2].toString() + ')';
                    var wx = icol * canvasWidth / pgm.ncols;
                    var hy =  canvasHeight - (1 + irow) * canvasHeight / pgm.nrows ;
                    canvasContext.fillRect(wx, hy, rectWidth, rectHeight);
                }    
            }
            if (device.autoModeAction.value < 4) 
            {

                canvasContext.lineWidth = 6;
                canvasContext.strokeStyle = '#ffff00';
                var ellipseWidth =  device.faceRegionWidth.value * canvasWidth / pgm.ncols;
                var ellipseHeight =  device.faceRegionHeight.value * canvasHeight / pgm.nrows;

                drawEllipse(canvasContext, (canvasWidth - ellipseWidth) / 2, (canvasHeight -  ellipseHeight)/ 2, ellipseWidth, ellipseHeight);

                canvasContext.beginPath();
                canvasContext.moveTo((canvasWidth - 0.3 * ellipseWidth) / 2, canvasHeight / 2);
                canvasContext.lineTo((canvasWidth + 0.3 * ellipseWidth) / 2, canvasHeight / 2);
                canvasContext.stroke();
                canvasContext.beginPath();
                canvasContext.moveTo(canvasWidth / 2, (canvasHeight -  0.3 * ellipseHeight) / 2);
                canvasContext.lineTo(canvasWidth / 2, (canvasHeight +  0.3 * ellipseHeight) / 2);
                canvasContext.stroke();


                canvasContext.strokeStyle = '#00ff00';
                if (device.autoModeAction.value < 4) canvasContext.strokeStyle = '#ff0000';
                var faceEllipseWidth  =  device.faceLocation.value[1][1] -  device.faceLocation.value[0][1];
                var faceEllipseHeight =  device.faceLocation.value[1][0] -  device.faceLocation.value[0][0] ;
                var faceEllipseCol    = (device.faceLocation.value[1][1] +  device.faceLocation.value[0][1]) / 2 ;
                var faceEllipseRow    = (device.faceLocation.value[1][0] +  device.faceLocation.value[0][0]) / 2 ;
                faceEllipseCol = faceEllipseCol - faceEllipseWidth  / 2.0;
                faceEllipseRow = pgm.nrows - (faceEllipseRow + faceEllipseHeight / 2.0);
                faceEllipseWidth  =  faceEllipseWidth  * canvasWidth  / pgm.ncols;
                faceEllipseHeight =  faceEllipseHeight * canvasHeight / pgm.nrows;
                faceEllipseCol    =  faceEllipseCol    * canvasWidth  / pgm.ncols;
                faceEllipseRow    =  faceEllipseRow    * canvasHeight / pgm.nrows;
                drawEllipse(canvasContext, faceEllipseCol, faceEllipseRow, faceEllipseWidth, faceEllipseHeight);

                canvasContext.beginPath();
                canvasContext.moveTo(faceEllipseCol + (faceEllipseWidth / 2) - 0.15 * faceEllipseWidth, faceEllipseRow + faceEllipseHeight / 2);
                canvasContext.lineTo(faceEllipseCol + (faceEllipseWidth / 2) + 0.15 * faceEllipseWidth, faceEllipseRow + faceEllipseHeight / 2);
                canvasContext.stroke();
                canvasContext.beginPath();
                canvasContext.moveTo(faceEllipseCol + faceEllipseWidth / 2, faceEllipseRow + (faceEllipseHeight / 2) - 0.15 * faceEllipseHeight);
                canvasContext.lineTo(faceEllipseCol + faceEllipseWidth / 2, faceEllipseRow + (faceEllipseHeight / 2) + 0.15 * faceEllipseHeight);
                canvasContext.stroke();

            }
        }
        function getRGB(intensity)
        {
          var rgb = [0,0,0];
//plasma
          var rgbCoeff = [
              [  48.6, 349.0,    5.72, -172.0],
              [-164.0, 705.0, -667.0 ,  379.0],
              [ 135.0, 332.0, -976.0,   536.0]];
          var intensityStart = [0.0, 0.3176, 0.0];
//viridis
/*
          var rgbCoeff = [
              [  72.7, 161.0, -928.0,  962.0],
              [-0.316, 334.0,  -62.8 , -35.3],
              [  89.3, 296.0, -379.0,  -11.5]];
          var intensityStart = [0.0, 0.0117, 0.0];
*/
//inferno
/* 
          var rgbCoeff = [
              [   0,0,  343.0,  264.0, -376.0],
              [   5.0,  -69.7,  320.0,   -1.1],
              [   0.0, 1193.0,-2957.0, 1939.0]];
          var intensityStart = [0.0, 0.0, 0.0];
*/
          var power = [1.0, 0.0, 0.0, 0.0];
          for (var ii = 1; ii < 4; ++ii) power[ii] = power[ii - 1] * intensity;
          for (var irgb = 0; irgb < 3; ++irgb)
          {
              if (intensity >= intensityStart[irgb])
              {
                  for (var ipow = 0; ipow < 4; ++ipow)
                  {
                      rgb[irgb] = rgb[irgb] + rgbCoeff[irgb][ipow] * power[ipow];
                  }
              }
              if (rgb[irgb] <   0) rgb[irgb] =   0;
              if (rgb[irgb] > 255) rgb[irgb] = 255;
         }
          return rgb;
        }
        function getRGBRainBow(intensity)
        {
          var rgb = [0,0,0];
          if ( (0 <= intensity) && (intensity <= 0.25) )
          {
            rgb[0] = 0;
            rgb[1] = Math.floor(255 * intensity * 4);    
            rgb[2] = 255;
          }
          if ( (0.25 < intensity) && (intensity <= 0.50) )
          {
            rgb[0] = 0;
            rgb[1] = 255;    
            rgb[2] = Math.floor(255 * (0.50 - intensity) * 4);
          }
          if ( (0.50 < intensity) && (intensity <= 0.75) )
          {
            rgb[0] = Math.floor(255 * (intensity - 0.50) * 4);
            rgb[1] = 255;    
            rgb[2] = 0;
          }
          if ( (0.75 < intensity) && (intensity <= 1.0) )
          {
            rgb[0] = 255;
            rgb[1] = Math.floor(255 * (1.0 - intensity) * 4);    
            rgb[2] = 0;
          }
          return rgb;
        }
        function clearImage()
        {
            var canvas = document.getElementById("flirCanvas");
            var canvasContext = canvas.getContext("2d");
            var canvasWidth  = Number($('#flirCanvas').attr('width'));
            var canvasHeight = Number($('#flirCanvas').attr('height'));
            canvasContext.clearRect(0, 0, canvasWidth, canvasHeight);        
            for (var ibox = 0; ibox < 9; ++ibox)
            {
                var pbox = 0.0;
                $('#box' + (ibox + 1).toString() + 'Label').text(' ');
            } 
        }
        function drawEllipse(ctx, x, y, w, h) 
        {
            var kappa = .5522848,
              ox = (w / 2) * kappa, // control point offset horizontal
              oy = (h / 2) * kappa, // control point offset vertical
              xe = x + w,           // x-end
              ye = y + h,           // y-end
              xm = x + w / 2,       // x-middle
              ym = y + h / 2;       // y-middle

            ctx.beginPath();
            ctx.moveTo(x, ym);
            ctx.bezierCurveTo(x, ym - oy, xm - ox, y, xm, y);
            ctx.bezierCurveTo(xm + ox, y, xe, ym - oy, xe, ym);
            ctx.bezierCurveTo(xe, ym + oy, xm + ox, ye, xm, ye);
            ctx.bezierCurveTo(xm - ox, ye, x, ym + oy, x, ym);
            //ctx.closePath(); // not used correctly, see comments (use to close off open path)
            ctx.stroke();
        }

