return null;

var jsonPretty = JSON.stringify(msg.payload,null,2);
var filename = 'cube-A-settings.json';
return {topic: msg.payload, payload:jsonPretty, 'filename':filename};

scalarAlarmScan(msg.payload, 'setting');
flow.set('device', msg.payload); 
flow.set('newSetting', 1); 
return msg;

function getActionArray(device, type, actionType)
{
    var actionArray = [];
    var keys = Object.keys(device);
    for (var ii = 0; ii < keys.length; ++ii)
    {
        if (device[keys[ii]].hasOwnProperty('type'))
        {
            if (device[keys[ii]].type == type)
            {
                if (device[keys[ii]].action == actionType)
                {
                    actionArray.push(device[keys[ii]]);
                }
            }
        }
    }
    return actionArray;
}
function scalarAlarmScan(device, actionType)
{
    var actionArray = getActionArray(device, 'scalar', actionType);
    if (actionArray.length > 0)
    {
        for (var ii = 0; ii < actionArray.length; ++ii)
        {
            actionArray[ii].alarm.value = 0;
            if (!isNaN(actionArray[ii].value))
            {
                if (actionArray[ii].value < actionArray[ii].alarm.limits.low)  actionArray[ii].alarm.value = 1;
                if (actionArray[ii].value < actionArray[ii].alarm.limits.lolo) actionArray[ii].alarm.value = 3;
                if (actionArray[ii].value > actionArray[ii].alarm.limits.high) actionArray[ii].alarm.value = 2;
                if (actionArray[ii].value > actionArray[ii].alarm.limits.hihi) actionArray[ii].alarm.value = 4;
            }
        }
    }
}

device = flow.get('device');
device.imageData.value = null;
var pgm = flow.get('pgm');
var actionMsg = null;
if (pgm != null)
{
    device.archiveData.value = 1;
    flow.set('lastImageTime',new Date().getTime());
    var imageAnal = imageAnalysis(JSON.parse(JSON.stringify(pgm)), device.processImage.value, 15,45,30,50, device.archiveMinTemp.value);
//    var imageAnal = imageAnalysis(pgm, device.processImage.value, 15,45,30,50, device.archiveMinTemp.value);

    device.pixelMin.value = Math.round(10 *imageAnal.minPix ) / 10;
    device.pixelMax.value  = Math.round(10 *imageAnal.maxPix ) / 10;
    device.imageData.value = pgm;
    device.timeStamp = pgm.timeStamp;
    device.autoModeAction.value = 0;
    device.bodyTemp.value = 0;
    if (device.triggerMode.value == 0)
    {
        device.bodyTemp.value = Math.round(10 * (imageAnal.maxBoxTempC + device.offsetTemp.value)) / 10;
        if(imageAnal.boxPercent > device.archivePixelPercent.value) device.autoModeAction.value = 1;

        if (device.autoModeAction.value == 0)
        {
            actionMsg = {topic:'actionMsg', payload:0};
            device.bodyTemp.value = 0;
        }
        else
        {
            if ((device.bodyTemp.alarm.limits.low < device.bodyTemp.value) && (device.bodyTemp.value < device.bodyTemp.alarm.limits.high))
            {
                actionMsg = {topic:'actionMsg', payload:1};
                device.autoModeAction.value = 1;
            }
            if ((device.bodyTemp.alarm.limits.high < device.bodyTemp.value) && (device.bodyTemp.value < device.bodyTemp.alarm.limits.hihi))
            {
                actionMsg = {topic:'actionMsg', payload:2};
                device.autoModeAction.value = 2;
            }
            if ((device.bodyTemp.alarm.limits.low > device.bodyTemp.value) || (device.bodyTemp.value > device.bodyTemp.alarm.limits.hihi))
            {
                actionMsg = {topic:'actionMsg', payload:3};
                device.autoModeAction.value = 3;
            }
        }
    }
}
else
{
//    if (device.ultraSoundDistance.value != 400) return null;
}

device.linkQuality.value     = global.get('linkQuality');
device.signalLevel.value     = global.get('signalLevel');
scalarAlarmScan(device, 'reading');
var toppy = global.get('project') + '/' + global.get('deviceTypeA') + '/' + global.get('deviceNameA') + '/reading';
