function blurImage(inputImage)
{
    var kernal = [[1,2,1], [2,4,2], [1,2,1]];
    var blurImage = [];
    var nrows = inputImage.length;
    var ncols = inputImage[0].length;
    for (var irow = 0; irow < nrows; ++irow)
    {
        blurImage[irow] = [];
        for (var icol = 0; icol < ncols; ++icol)
        {
            if ((irow == 0) || (irow == (nrows - 1)))
            {
                blurImage[irow][icol] =  inputImage[irow][icol];
            }
            else
            {
                if ((icol == 0) || (icol == (ncols - 1)))
                {
                    blurImage[irow][icol] =  inputImage[irow][icol];
                }
                else
                {
                    blurImage[irow][icol] = 0;
                    for (var ikrow = 0; ikrow < 3; ++ikrow)
                    {
                        for (var ikcol = 0; ikcol < 3; ++ikcol)
                        {
                            blurImage[irow][icol] = blurImage[irow][icol] + inputImage[irow + ikrow - 1][icol + ikcol - 1] * kernal[ikrow][ikcol];
                        }
                    }
                    blurImage[irow][icol] = Math.round(blurImage[irow][icol] / 16.0);
                }
            }
        }
    }
    return blurImage;
}
function imageAnalysis(imagePgm,blurImageFlag, boxRowMin,boxRowMax,boxColMin,boxColMax, boxThresholdC)
{
    var imageArray = imagePgm.imageArray;
    if (blurImage == 1) imageArray = blurImage(imagePgm.imageArray);
    var nrows = imageArray.length;
    var ncols = imageArray[0].length;
    var minPix = 500.0;
    var maxPix = -273.0;
    var nrows = imageArray.length;
    var ncols = imageArray[0].length;
    var boxPercent = 0;
    var maxBoxTempC = -273.0;
    var boxCnt = 0;
    for (var irow = 0; irow < nrows; ++irow)
    {
        for (var icol = 0; icol < ncols; ++icol)
        {
            var tempC = (((pgm.maxval - pgm.minval) * imageArray[irow][icol] / 255.0) + pgm.minval  - 27315) / 100;
            if (minPix > tempC) minPix = tempC;
            if (maxPix < tempC) maxPix = tempC;
            if ((irow >= boxRowMin) && (irow <= boxRowMax) )
            {
                if ((icol >= boxColMin) && (icol <= boxColMax) )
                {
                    if (tempC > boxThresholdC) ++boxPercent;
                    if (tempC > maxBoxTempC) maxBoxTempC = tempC;
                    ++boxCnt
                    imageArray[irow][icol] = 0.0;
                }
            }
        }
    }
    if (boxCnt > 0)
    {
        boxPercent = Math.round(1000 * boxPercent / boxCnt) / 10;
    }
    return {'minPix': minPix, 'maxPix' : maxPix, 'boxPercent' : boxPercent, 'maxBoxTempC': maxBoxTempC};
}

