var linkQuality = 0;
var signalLevel = -100;
var ssid = "none";

var info = msg.payload;
var linkStartIndex  = info.indexOf('Link Quality=');
if (linkStartIndex >= 0)
{
    var levelStartIndex = info.indexOf('Signal level=');
    var linkStopIndex   = info.indexOf('/70');
    linkQuality = Number(info.substring(linkStartIndex + 13, linkStopIndex));
    signalLevel =  Number(info.substring(levelStartIndex + 13, levelStartIndex + 17));
    ssid = info.substring(info.indexOf('ESSID') + 7);
    ssid = ssid.substring(0,ssid.indexOf('"'));
}
var val = {linkQuality : linkQuality, signalLevel: signalLevel, ssid:ssid};
global.set('linkQuality',linkQuality);
global.set('signalLevel', signalLevel);
global.set('ssid', ssid);
return {topic :'test',payload : val};

if (msg.payload.length < 1)
{
    var errCount = context.get('errCount');
    if (errCount == undefined) errCount = 0;
    ++errCount
    if (errCount < 5)
    {
        context.set('errCount',errCount);
        return [{topic:'takeImage',payload:true, delay:flow.get('device').imageDelay.value}, null];
    }
    else
    {
        context.set('errCount',0);
        node.warn("Resetting Camera");
        flow.set('resettingCamera',true);
        return [null, {topic:'resetCamera',payload:true}];
    }
}
else
{
    context.set('errCount',0);
}
var imgArray = [];
var dataView     = new DataView(new ArrayBuffer(2));
for (var irow = 0; irow < 4800; ++irow)
{
        dataView.setUint8(0, msg.payload[irow * 2]);
        dataView.setUint8(1, msg.payload[irow * 2 + 1]);
        imgArray[irow] = dataView.getUint16(0);
}
var minval = imgArray[0];
var maxval = imgArray[0];
for (var irow = 0; irow < 4800; ++irow)
{
        if ( minval > imgArray[irow]) minval = imgArray[irow];
        if ( maxval < imgArray[irow]) maxval = imgArray[irow];
}
if (minval == 0) return [{topic:'takeImage',payload:true, delay:flow.get('device').imageDelay.value}, null];
var imgArray2D = [];
var deltaVal = maxval - minval;
if (deltaVal == 0) deltaVal = 1;
var numPixOverThreshold = 0;
var tempThreshold = flow.get('device').archiveMinTemp.value * 100 + 27315;
for (var irow = 0; irow < 60; ++irow)
{
    imgArray2D[irow] = [];
    for (var icol = 0; icol < 80; ++icol)
    {
        if (flow.get('device').flipVertical.value == 0)
        {
            if (flow.get('device').flipHorizontal.value == 0)
            {
                imgArray2D[irow][icol] = Math.floor(256.0 * (imgArray[irow * 80 + icol] - minval) / deltaVal);
            }
            else
            {
                imgArray2D[irow][icol] = Math.floor(256.0 * (imgArray[irow * 80 + 79 - icol] - minval) / deltaVal);
            }
        }
        else
        {
            if (flow.get('device').flipHorizontal.value == 0)
            {
                imgArray2D[irow][icol] = Math.floor(256.0 * (imgArray[(59 - irow) * 80 + icol] - minval) / deltaVal);
            }
            else
            {
                imgArray2D[irow][icol] = Math.floor(256.0 * (imgArray[(59 - irow) * 80 + 79 - icol] - minval) / deltaVal);
            }
        }
        if (imgArray[irow * 80 + icol] > tempThreshold) ++numPixOverThreshold
    }    
}

var pgm = 
{
    'type'      : 'P2',
    'timeStamp' : new Date().getTime(),
    'nrows'     : 60,
    'ncols'     : 80,
    'minval'    : minval,
    'maxval'    : maxval,
    'imageArray': imgArray2D
};
if (numPixOverThreshold < 10) pgm = null;
flow.get('device').manualTrigger.value = 0;
flow.set('pgm',pgm);
return [{topic:'takeImage',payload:true, delay:flow.get('device').imageDelay.value}, null];

var newMsg = {fill: msg.status.fill, pid:msg.status.text}
flow.set('cameraProcess', newMsg);
return null;

var process = flow.get('cameraProcess');
var newMsg = null;
if (process.pid != undefined)
{
    var pid = Number(process.pid.substring(4)) + 1;
    newMsg = {topic:'killProcess',payload:pid}
    node.warn('Camera Hang')
}
return newMsg;

return {
    topic   : msg.payload.mqttMessage.topic, 
    payload : msg.payload.mqttMessage.payload, 
    userID  : msg.payload.userID };

if (flow.get('device') == undefined) return [null,null];
if (flow.get('resettingCamera') == undefined) return [null,null];
flow.get('device').archiveData.value = 0;
flow.set('pgm',null);
flow.get('device').ultraSoundDistance.value = msg.payload;
var minTime = flow.get('device').imageDelay.value;
var range = flow.get('device').ultraSoundRange.value;

var lastImageTime = flow.get('lastImageTime');
if (lastImageTime == undefined) 
{
    lastImageTime = new Date().getTime();
    flow.set('lastImageTime',lastImageTime);
}
var nowTime = new Date().getTime();
if ((nowTime - lastImageTime) > minTime)
{
    if (msg.payload < range)
    {
        if (!flow.get('resettingCamera') && !flow.get('gateInUse')) return [msg, null];
    }
}
return [null, msg];

if (flow.get('device').triggerMode.value != 0) return null;
var distance = Number(msg.payload);
var lastDistance = context.get('lastDistance');
context.set('lastDistance',distance);
if (lastDistance == undefined)
{
    return null;
}
var range = flow.get('device').ultraSoundRange.value;
if (lastDistance > range) return null;
if (distance > range) return null;
return {topic:'targetDistance',payload:distance};

flow.set('resettingCamera',true);
flow.set('lastImageTime',new Date().getTime());
var voiceWarnings = [
    [""        , ""        ,""            ,""          ,""         ,""                   ,""],
    ["Welcome" ,"Vlkommen"," ","Bienvenido","Benvenuto","Herzlich willkommen","Bienvenu"],
    ["Good bye","Hej d"   ," "   ,"Adis"     ,"Addio"    ,"Auf Wiedersehen"    ,"Au revoir"],
    [""        ,""         ,""            ,""          ,""         ,""                   ,""] ];
flow.set('voiceWarnings',voiceWarnings);
var languageFlags = [' -v en',' -v sv',' -v el',' -v es',' -v it',' -v de',' -v fr'];
flow.set('languageFlags',languageFlags);
return msg;

