                pgm = JSON.parse(JSON.stringify(device.imageData.value));
                var processImage = 1;
                try {processImage = device.processImage.value;} catch(err) {processImage = 1;}
                switch (processImage)
                {
                    case 0:  
                        imageArray = pgm.imageArray;
                        break;
                    case 1:  
                        imageArray = blurImage(pgm.imageArray);
                        break;
                    default: 
                        imageArray = blurImage(pgm.imageArray);
