        function setDevice()
        {
            var attr = device[editedAttribute];
            if (isNaN($('#loloAlarm').val()) 
                || isNaN($('#lowAlarm').val()) 
                || isNaN($('#highAlarm').val()) 
                || isNaN($('#hihiAlarm').val()))
