            var lowValue = 0;
            var mediumValue = 0;
            var highValue = 0;

            var lowColor = barConfig.backgroundColor;
            var mediumColor = barConfig.backgroundColor;
            var highColor = barConfig.backgroundColor;
            var valueColor = barConfig.backgroundColor; 

            if ( barConfig.value <= barConfig.lolo)
            {
                lowValue = barConfig.lolo;
                lowColor = barConfig.lowColor;
                valueColor = lowColor;
            }
            if ( (barConfig.lolo <= barConfig.value) && (barConfig.value < barConfig.low) )
            {
                lowValue = barConfig.value;
                lowColor = barConfig.lowColor;
                valueColor = lowColor;
            }
            if ( (barConfig.low  <= barConfig.value) && (barConfig.value < barConfig.high) )
            {
                lowValue = barConfig.low;
                mediumValue = barConfig.value - barConfig.low; 
                lowColor = barConfig.mediumColor;
                mediumColor = barConfig.mediumColor;
                valueColor = mediumColor;

            }
            if ( (barConfig.high <= barConfig.value) && (barConfig.value < barConfig.hihi) )
            {
                lowValue = barConfig.low;
                mediumValue = barConfig.high - barConfig.low;
                highValue = barConfig.value - barConfig.high;
                lowColor = barConfig.highColor;
                mediumColor = barConfig.highColor;
                highColor = barConfig.highColor;
                valueColor = highColor;
            }
            if ( barConfig.hihi <= barConfig.value)
            {
                lowValue = barConfig.low;
                mediumValue = barConfig.high - barConfig.low; 
                highValue = barConfig.hihi - barConfig.high; 
                lowColor = barConfig.highColor;
                mediumColor = barConfig.highColor;
                highColor = barConfig.highColor;
                valueColor = highColor;
            }

            var lowBar = 
            {
                y: ['', '-'],
                x: [ barConfig.low, lowValue],
                type: 'bar',
                orientation: 'h',
                showlegend: false,
                marker:{color: [barConfig.lowColor, lowColor]},
              width: [0.1, 1.0],
            };
            var mediumBar = 
            {
                y: ['', '-'],
                x: [barConfig.high - barConfig.low, mediumValue],
                type: 'bar',
                orientation: 'h',
                showlegend: false,
                marker:{color: [barConfig.mediumColor, mediumColor]},
              width: [0.1, 1.0],
            };
            var highBar = 
            {
                y: ['', '-'],
                x: [barConfig.hihi - barConfig.high, highValue],
                orientation: 'h',
                type: 'bar',
                showlegend: false,
                marker:{color: [barConfig.highColor, highColor]},
              width: [0.1, 1.0],
            };
            var layout = 
                {
                    barmode: 'stack',
                    plot_bgcolor:barConfig.backgroundColor,
                    paper_bgcolor:barConfig.backgroundColor,
                    margin: 
                        {
                            t: 1, //top margin
                            l: 10, //left margin
                            r: 10, //right margin
                            b: 30 //bottom margin
                        },

                    height: barConfig.height,
                    title: 
                        {
                            text: barConfig.value.toString() + ' ' + barConfig.unit,
//                            font: {color: valueColor, size:barConfig.textsize},
                            font: {color: '#000000', size:45},
                        },
                    yaxis: 
                        {
                            tickfont: {color:valueColor, size:barConfig.textsize}
                        },
                    xaxis: 
                        {
                            range: [barConfig.lolo, barConfig.hihi],
                            tickfont: {color:barConfig.labelColor, size:barConfig.textsize},
                            gridcolor       : barConfig.gridColor

                        },
                };
           Plotly.newPlot(barConfig.htmlId, [lowBar, mediumBar, highBar], layout, {'displayModeBar': false});
        }

func:
if (flow.get('device') == undefined) return null;
if (flow.get('resettingCamera') == undefined) return null;
flow.get('device').archiveData.value = 0;
flow.set('pgm',null);
return msg;

if (flow.get('device') == undefined) return null;
if (flow.get('resettingCamera') == undefined) return null;
if (flow.get('device').triggerMode.value != 1) return null;
flow.get('device').archiveData.value = 0;
flow.set('pgm',null);
var minTime = flow.get('device').imageDelay.value;

var lastImageTime = flow.get('lastImageTime');
if (lastImageTime == undefined) 
{
    lastImageTime = new Date().getTime();
    flow.set('lastImageTime',lastImageTime);
}
var nowTime = new Date().getTime();
if ((nowTime - lastImageTime) > minTime)
{
    if (!flow.get('resettingCamera')) return msg;
}
return null;

var ib;
var dataView     = new DataView(new ArrayBuffer(4));
var newSettingDone = 0;
var cubeInit = 0;

for (ib = 0; ib < 4; ++ib)   dataView.setUint8(ib, msg.payload[0 * 4 + 3 - ib]);
cubeInit = dataView.getInt32(0);
for (ib = 0; ib < 4; ++ib)   dataView.setUint8(ib, msg.payload[1 * 4 + 3 - ib]);
newSettingDone = dataView.getInt32(0);

var newMsg = {topic:'userData', payload:[]};
for (var ii = 8; ii < msg.payload.length; ++ii) newMsg.payload[ii - 8] = msg.payload[ii];

if (newSettingDone > 0) 
{
    flow.get('gate').newSetting = 0;
}
if(cubeInit == 1) 
{
    flow.get('gate').noStepsToTurn = 0;
    flow.get('gate').stayAwakeAfterRotation = 0;
    flow.get('gate').newSetting = 1;
}
//node.warn('newSettingDone = ' + newSettingDone.toString());
//node.warn('cubeInit = ' + cubeInit.toString());

return newMsg;

var bufsize = 24;
var buffer = new ArrayBuffer(bufsize);
var settingsView = new DataView(buffer);
settingsView.setInt32(0, flow.get('gate').newSetting, true);
settingsView.setInt32(4, flow.get('gate').noStepsToTurn, true);
settingsView.setInt32(8, flow.get('gate').motorDirection, true);
settingsView.setInt32(12, flow.get('gate').stepDelayuS, true);
settingsView.setInt32(16, flow.get('gate').stayAwakeAfterRotation, true);
settingsView.setInt32(20, flow.get('gate').loopDelay, true);

return {topic:'settings',payload:Buffer.from(buffer)};

var ib;
var dataView     = new DataView(new ArrayBuffer(4));

for (ib = 0; ib < 4; ++ib)   dataView.setUint8(ib, msg.payload[0 * 4 + 3 - ib]);
var stepLocation = dataView.getInt32(0);


return {topic : 'reading', payload : stepLocation};

if(msg.payload == 1) 
{
    flow.get('gate').noStepsToTurn = 50;
    flow.get('gate').stayAwakeAfterRotation = 1;
    flow.get('gate').motorDirection = 0;
    flow.get('gate').newSetting = 1;
}
if(msg.payload == -1) 
{
    flow.get('gate').noStepsToTurn = 50;
    flow.get('gate').stayAwakeAfterRotation = 1;
    flow.get('gate').motorDirection = 1;
    flow.get('gate').newSetting = 1;
}
if(msg.payload == 0) 
{
    flow.get('gate').noStepsToTurn = 0;
    flow.get('gate').stayAwakeAfterRotation = 0;
    flow.get('gate').motorDirection = 0;
    flow.get('gate').newSetting = 1;
}
return null;

var gate = 
{
    noStepsToTurn : 0,
    motorDirection :1,
    stepDelayuS : 30000,
    stayAwakeAfterRotation : 0,
    loopDelay : 1000,
    newSetting : 1
}
flow.set('gate',gate);
flow.set('gateInUse',false);
return msg;

if (flow.get('device').gateMode.value == 0) return null;
if (flow.get('gateInUse')) return null;
flow.set('gateInUse',true);
msg.payload = 1;
return msg;

if (flow.get('device').gateMode.value == 0) 
{
    if (flow.get('gateInUse')) return null;
    flow.set('gateInUse',true);
}
var languageFlag = flow.get('languageFlags')[flow.get('device').language.value];
msg.payload = languageFlag + ' "' + flow.get('device').bodyTemp.value.toString() + '" --stdout | aplay'
return msg;

var burstCount = context.get('burstCount');
if (burstCount == undefined) burstCount = 0;
if (msg.topic == 'tooHotAlarm') burstCount = 0;
++burstCount;
context.set('burstCount',burstCount)
if (burstCount < 20) return msg;
return null;

if (flow.get('gateInUse')) return null;
flow.set('gateInUse',true);
var languageFlag = flow.get('languageFlags')[flow.get('device').language.value];
msg.payload = languageFlag + ' "' + flow.get('device').bodyTemp.value.toString() + '" --stdout | aplay'
return msg;

var languageFlag = flow.get('languageFlags')[flow.get('device').language.value];
msg.payload = languageFlag + ' "' + flow.get('voiceWarnings')[flow.get('device').autoModeAction.value][flow.get('device').language.value] + '" --stdout | aplay'
return msg;

var languageFlag = flow.get('languageFlags')[flow.get('device').language.value];
msg.payload = languageFlag + ' "' + flow.get('voiceWarnings')[flow.get('device').autoModeAction.value][flow.get('device').language.value] + '" --stdout | aplay'
return msg;

if (flow.get('device').gateMode.value == 1) return null;
flow.set('gateInUse',false);
msg.payload = 0;
return msg;
