    linkQuality = Number(info.substring(linkStartIndex + 13, linkStopIndex));
    signalLevel =  Number(info.substring(levelStartIndex + 13, levelStartIndex + 17));
    ssid = info.substring(info.indexOf('ESSID') + 7);
    ssid = ssid.substring(0,ssid.indexOf('"'));
}
