        function restartTray()
        {
            optionDialog('Danger! Will Robinson, Danger!', 'Restart Tray', 'This should not be done lightly', ['Reset Tray','Cancel'], [function (){restartTrayButtonPress();}, function (){$( "#optionDialog" ).dialog( "close" );}]);
        }
        function restartTrayButtonPress()
        {
            $( "#optionDialog" ).dialog( "close" );
            var mqttMessage = 
            {
                topic   : restfulDevice.project + '/' +  restfulDevice.deviceType + '/' +  restfulDevice.name + '/restartTray',
                payload :  1
            };
            sendSettingToMqtt(mqttMessage);
        }

