        function editDevice(attrName)
        {
            $( "#editDeviceDialog" ).dialog( "option", "title", "Edit"); 
            editedAttribute = attrName;
            $( "#editDeviceDialogAttribute").html(attrName);

            var attr = device[attrName];
            $( "#loloAlarm").val(attr.alarm.limits.lolo);
            $( "#lowAlarm").val(attr.alarm.limits.low);
            $( "#highAlarm").val(attr.alarm.limits.high);
            $( "#hihiAlarm").val(attr.alarm.limits.hihi);
            $( "#notifyName").val(attr.alarm.notify.name);
            $( "#notifyNumber").val(attr.alarm.notify.number);

            $( "#editDeviceDialog" ).dialog( "open" );
        }
        function setDevice()
        {
            var attr = device[editedAttribute];
            if (isNaN($('#loloAlarm').val()) 
                || isNaN($('#lowAlarm').val()) 
                || isNaN($('#highAlarm').val()) 
                || isNaN($('#hihiAlarm').val()))
            {
                acknowledgeDialog('Error', 'Entry not a number!', editedAttribute);
                editedAttribute = '';
                return;
            }
            editedAttribute = '';

            attr.alarm.limits.lolo = Number($( "#loloAlarm").val());
            attr.alarm.limits.low = Number($( "#lowAlarm").val());
            attr.alarm.limits.high = Number($( "#highAlarm").val());
            attr.alarm.limits.hihi = Number($( "#hihiAlarm").val());
            attr.alarm.notify.number =  $( "#notifyNumber").val();
            attr.alarm.notify.name = $( "#notifyName").val();
            var mqttMessage = 
            {
                topic   : mqttDevice.project + '/' +  mqttDevice.deviceType + '/' +  mqttDevice.name + '/setting',
                payload :  device
            };
            sendSettingToMqtt(mqttMessage);
        }

// StripChart Widget
        class StripChart
        {
           constructor(plotConfig) 
            {
                this.plotConfig = plotConfig;
                this.plotStartTime = new Date().getTime();
                this.layout =
                {
                    showlegend  : true,
                    legend      : { x: 0, y: 1.15},
                    height      : plotConfig.height,
                    width       : plotConfig.width,
                    xaxis       :
                    {
//                        title: 'Seconds since ' + new Date(this.plotStartTime).toLocaleString(),
                        type: 'date'
                    },
                    yaxis:
                    {
                        title           : plotConfig.trace1Title,
                        titlefont       : {color: plotConfig.trace1Color},
                        tickfont        : {color:plotConfig.trace1Color},
                        gridcolor       : plotConfig.trace1GridColor,
                        zerolinecolor   : plotConfig.trace1GridColor,
                        linecolor       : plotConfig.trace1GridColor,
//                        range: [0, 2]

                    },
                    yaxis2:
                    {
                        title           : plotConfig.trace2Title,
                        titlefont       : {color: plotConfig.trace2Color},
                        tickfont        : {color:plotConfig.trace2Color},
                        gridcolor       : plotConfig.trace2GridColor,
                        zerolinecolor   : plotConfig.trace2GridColor,
                        linecolor       : plotConfig.trace2GridColor,
                        overlaying: 'y',
                        side: 'right',
//                        range: [0, 2]
                    },
                    margin: 
                        {
                            t: 30, //top margin
        //                    l: 0, //left margin
        //                    r: 0, //right margin
        //                    b: 20 //bottom margin
                        },
                };
                this.trace1 = 
                {
                    x: [],
                    y: [],
                    name: 'Max Temp',
                    yaxis: 'y1',
                    type: 'scatter',
                    mode: 'markers',
                    marker: {
                        color: 'red',
                        size: 10,
                        symbol: 'circle'
                    }
                };
                this.trace2 = 
                {
                    x: [],
                    y: [],
                    name: 'Min Temp',
                    yaxis: 'y1',
                    type: 'scatter',
                    mode: 'markers',
                    marker: {
                        color: 'green',
                        size: 10,
                        symbol: 'square'
                    }
                };
                this.trace3 = 
                {
                    x: [],
                    y: [],
                    name: 'Distance',
                    yaxis: 'y2',
                    type: 'scatter',
                    mode: 'markers',
                    marker: {
                        color: this.plotConfig.trace2Color,
                        size: 5,
                        symbol: 'diamond'
                    }
                };
            }
            add1TracePoints(trace3Pt)
            {
                var now = new Date().getTime();
                var plotBeginning = now - this.plotConfig.plotLength;
                var removeDone = false;
                while(!removeDone)
                {
                    if (this.trace1.x[0] < plotBeginning)
                    {
                        this.trace1.x.shift();
                        this.trace1.y.shift();
                    }
                    else
                    {
                        removeDone = true;
                    }
                }
                removeDone = false;
                while(!removeDone)
                {
                    if (this.trace2.x[0] < plotBeginning)
                    {
                        this.trace2.x.shift();
                        this.trace2.y.shift();
                    }
                    else
                    {
                        removeDone = true;
                    }
                }
                removeDone = false;
                while(!removeDone)
                {
                    if (this.trace3.x[0] < plotBeginning)
                    {
                        this.trace3.x.shift();
                        this.trace3.y.shift();
                    }
                    else
                    {
                        removeDone = true;
                    }
                }

                this.trace3.x.push(now);
                this.trace3.y.push(trace3Pt);
                Plotly.newPlot(this.plotConfig.htmlId, [this.trace1, this.trace2, this.trace3], this.layout);
            }
            add3TracePoints(trace1Pt, trace2Pt, trace3Pt)
            {
                var now = new Date().getTime();
                var plotBeginning = now - this.plotConfig.plotLength;
                var removeDone = false;
                while(!removeDone)
                {
                    if (this.trace1.x[0] < plotBeginning)
                    {
                        this.trace1.x.shift();
                        this.trace1.y.shift();
                    }
                    else
                    {
                        removeDone = true;
                    }
                }
                removeDone = false;
                while(!removeDone)
                {
                    if (this.trace2.x[0] < plotBeginning)
                    {
                        this.trace2.x.shift();
                        this.trace2.y.shift();
                    }
                    else
                    {
                        removeDone = true;
                    }
                }
                removeDone = false;
                while(!removeDone)
                {
                    if (this.trace3.x[0] < plotBeginning)
                    {
                        this.trace3.x.shift();
                        this.trace3.y.shift();
                    }
                    else
                    {
                        removeDone = true;
                    }
                }

                this.trace1.x.push(now);
                this.trace1.y.push(trace1Pt);
                this.trace2.x.push(now);
                this.trace2.y.push(trace2Pt);
                this.trace3.x.push(now);
                this.trace3.y.push(trace3Pt);
                Plotly.newPlot(this.plotConfig.htmlId, [this.trace1, this.trace2, this.trace3], this.layout);
            }
        }

