// Update Readings Javascript
        function updateDmaReadings()
        {
            if (device.imageData.value != null)
            {
                var style = 'font-size:200%; font-weight:200; color:#ffff00;';
                if ((device.triggerMode.value == 0) || (device.triggerMode.value == 1))
                {
                    $('#instruction1Id').text(instructions[device.language.value][0]);
                    $('#instruction2Id').text(instructions[device.language.value][1]);
                    $('#instruction3Id').text(instructions[device.language.value][2]);
                    $('#instruction1Id').show();
                    $('#instruction2Id').show();
                    $('#instruction3Id').show();
                    $('#tempComment').show();

                    if (device.autoModeAction.value == 0)
                    {
                        style = 'font-size:200%; font-weight:200; color:#ff00ff;';
                         $('#tempComment').attr("style", style);
                        $('#tempComment').text(warnings[device.autoModeAction.value][device.language.value]);
                    }
                    if (device.autoModeAction.value == 1)
                    {
                        style = 'font-size:200%; font-weight:200; background-color:#00ff00;color:black;';
                        $('#tempComment').attr("style", style);
                        $('#tempComment').text(warnings[device.autoModeAction.value][device.language.value]);
                    }
                    if (device.autoModeAction.value == 2)
                    {
                        style = 'font-size:200%; font-weight:200; background-color:#ff0000;color:black;';
                        $('#tempComment').attr("style", style);
                        $('#tempComment').text(warnings[device.autoModeAction.value][device.language.value]);
                    }
                    if (device.autoModeAction.value == 3)
                    {
                        style = 'font-size:200%; font-weight:200; color:#ff00ff;';
                        $('#tempComment').attr("style", style);
                        $('#tempComment').text(warnings[device.autoModeAction.value][device.language.value]);
                    }
                }
                else
                {
                    $('#tempComment').attr("style", style);
                    $('#tempComment').text("Surface Temperature");
                }
                lastUpdate = new Date().getTime();
            }
            if (device.imageData.value != null)
            {
                pgm = JSON.parse(JSON.stringify(device.imageData.value));
                imageArray = pgm.imageArray;
                plotImage();
                $('#tempBarPlot').show();
                var tempBarConfig = 
                    {
                        lolo            : device.bodyTemp.alarm.limits.lolo, 
                        low             : device.bodyTemp.alarm.limits.low, 
                        high            : device.bodyTemp.alarm.limits.high, 
                        hihi            : device.bodyTemp.alarm.limits.hihi, 
                        value           : device.bodyTemp.value, 
                        unit            : device.bodyTemp.unit, 
                        textsize        : 20,
                        label           : 'temp', 
                        lowColor        : '#0000ff', 
                        mediumColor     : '#00ff00',
                        highColor       : '#ff0000', 
                        backgroundColor : '#000000', 
                        labelColor      : '#ffffff', 
                        gridColor       : '#000000',
                        height          : 125, 
                        htmlId          : 'tempBarPlot'
                    };
                if (device.autoModeAction.value == 0)
                {
                    tempBarConfig.lowColor = '#000000';
                    tempBarConfig.mediumColor = '#000000';
                    tempBarConfig.highColor = '#000000';
                    tempBarConfig.labelColor = '#000000';
                }
                setHorzBarWidget(tempBarConfig);
            }
        }
        function plotImage()
        {
            $('#tempLegend').show();
            var rgb = [];
            var plow = (pgm.minval - 27315) / 100;
            var phigh = (pgm.maxval - 27315) / 100;
            var pdelta = phigh - plow;
            var canvas = document.getElementById("flirCanvas");
            var canvasContext = canvas.getContext("2d");

            var canvasWidth  = Number($('#flirCanvas').attr('width'));
            var canvasHeight = Number($('#flirCanvas').attr('height'));
            var rectWidth  = Math.round(canvasWidth / pgm.ncols);
            var rectHeight =  Math.round(canvasHeight / pgm.nrows);

            for (var irow = 0; irow < pgm.nrows; ++irow)
            {
                for (var icol = 0; icol < pgm.ncols; ++icol)
                {
                    var intensity = imageArray[irow][icol] / 256.0;
                    if (enhancementLevel == -2) intensity = Math.pow(intensity, 1/3.0);
                    if (enhancementLevel == -1) intensity = Math.pow(intensity, 1/2.0);
                    if (enhancementLevel ==  0) intensity = Math.pow(intensity, 1);
                    if (enhancementLevel ==  1) intensity = Math.pow(intensity, 2.0);
                    if (enhancementLevel ==  2) intensity = Math.pow(intensity, 3.0);
                    if (colorScale)
                    {
                        rgb = getRGB(intensity);
                        rgb[0] = Math.floor(rgb[0]);
                        rgb[1] = Math.floor(rgb[1]);
                        rgb[2] = Math.floor(rgb[2]);
                    }
                    else
                    {
                        rgb[0] = Math.floor(255 * intensity);
                        rgb[1] = Math.floor(255 * intensity);
                        rgb[2] = Math.floor(255 * intensity);
                    }
                    canvasContext.fillStyle = 'rgb(' + rgb[0].toString() + ', ' + rgb[1].toString() + ', ' + rgb[2].toString() + ')';
                    var wx = Math.round(icol * canvasWidth / pgm.ncols);
                    var hy =  Math.round(canvasHeight - (1 + irow) * canvasHeight / pgm.nrows) ;
                    canvasContext.fillRect(wx, hy, rectWidth, rectHeight);
                }    
            }
            if ((device.triggerMode.value == 0) || (device.triggerMode.value == 1)) 
            {

                canvasContext.strokeStyle = 'black';
                var ellipseWidth =  Math.round(device.faceRegionWidth.value * canvasWidth / pgm.ncols);
                var ellipseHeight =  Math.round(device.faceRegionHeight.value * canvasHeight / pgm.nrows);

                drawEllipse(canvasContext, (canvasWidth - ellipseWidth) / 2, (canvasHeight -  ellipseHeight)/ 2, ellipseWidth, ellipseHeight);
                drawEllipse(canvasContext, (canvasWidth - ellipseWidth - 3) / 2, (canvasHeight -  ellipseHeight - 3)/ 2, ellipseWidth + 3, ellipseHeight + 3);
                drawEllipse(canvasContext, (canvasWidth - ellipseWidth - 6) / 2, (canvasHeight -  ellipseHeight - 6)/ 2, ellipseWidth + 6, ellipseHeight + 6);

                canvasContext.strokeStyle = '#00ff00'
                if (device.autoModeAction.value == 0) canvasContext.strokeStyle = '#ff0000';
                var faceEllipseWidth  =  device.faceLocation.value[1][1] -  device.faceLocation.value[0][1];
                var faceEllipseHeight =  device.faceLocation.value[1][0] -  device.faceLocation.value[0][0] ;
                var faceEllipseCol    = (device.faceLocation.value[1][1] +  device.faceLocation.value[0][1]) / 2 ;
                var faceEllipseRow    = (device.faceLocation.value[1][0] +  device.faceLocation.value[0][0]) / 2 ;
                faceEllipseCol = faceEllipseCol - faceEllipseWidth  / 2.0;
                faceEllipseRow = pgm.nrows - (faceEllipseRow + faceEllipseHeight / 2.0);
                faceEllipseWidth  = Math.round( faceEllipseWidth  * canvasWidth  / pgm.ncols);
                faceEllipseHeight = Math.round( faceEllipseHeight * canvasHeight / pgm.nrows);
                faceEllipseCol    = Math.round( faceEllipseCol    * canvasWidth  / pgm.ncols);
                faceEllipseRow    = Math.round( faceEllipseRow    * canvasHeight / pgm.nrows);
                drawEllipse(canvasContext, faceEllipseCol, faceEllipseRow, faceEllipseWidth, faceEllipseHeight);


                if (device.autoModeAction.value > 0)
                {
                    canvasContext.fillStyle = '#000000';
                    for (var ipt = 0; ipt < 2; ++ipt)
                    {
                        var wx =  Math.round(device.eyeLocation.value[ipt][1] * canvasWidth / pgm.ncols);
                        var hy =  Math.round(canvasHeight - (1 + device.eyeLocation.value[ipt][0]) * canvasHeight / pgm.nrows) ;
//                        canvasContext.fillRect(wx - rectWidth, hy - rectHeight, 3 * rectWidth, 3 * rectHeight);
                        canvasContext.strokeStyle = '#000000'
                        canvasContext.beginPath();
                        canvasContext.arc(wx + 0.5 * rectWidth, hy + 0.5 * rectHeight,  rectWidth, 0, 6.2831854);
                        canvasContext.stroke();
                    }
                }

            }
        }
        function getRGB(intensity)
        {
          var rgb = [0,0,0];
//plasma
          var rgbCoeff = [
              [  48.6, 349.0,    5.72, -172.0],
              [-164.0, 705.0, -667.0 ,  379.0],
              [ 135.0, 332.0, -976.0,   536.0]];
          var intensityStart = [0.0, 0.3176, 0.0];
//viridis
/*
          var rgbCoeff = [
              [  72.7, 161.0, -928.0,  962.0],
              [-0.316, 334.0,  -62.8 , -35.3],
              [  89.3, 296.0, -379.0,  -11.5]];
          var intensityStart = [0.0, 0.0117, 0.0];
*/
//inferno
/* 
          var rgbCoeff = [
              [   0,0,  343.0,  264.0, -376.0],
              [   5.0,  -69.7,  320.0,   -1.1],
              [   0.0, 1193.0,-2957.0, 1939.0]];
          var intensityStart = [0.0, 0.0, 0.0];
*/
          var power = [1.0, 0.0, 0.0, 0.0];
          for (var ii = 1; ii < 4; ++ii) power[ii] = power[ii - 1] * intensity;
          for (var irgb = 0; irgb < 3; ++irgb)
          {
              if (intensity >= intensityStart[irgb])
              {
                  for (var ipow = 0; ipow < 4; ++ipow)
                  {
                      rgb[irgb] = rgb[irgb] + rgbCoeff[irgb][ipow] * power[ipow];
                  }
              }
              if (rgb[irgb] <   0) rgb[irgb] =   0;
              if (rgb[irgb] > 255) rgb[irgb] = 255;
         }
          return rgb;
        }
        function getRGBRainBow(intensity)
        {
          var rgb = [0,0,0];
          if ( (0 <= intensity) && (intensity <= 0.25) )
          {
            rgb[0] = 0;
            rgb[1] = Math.floor(255 * intensity * 4);    
            rgb[2] = 255;
          }
          if ( (0.25 < intensity) && (intensity <= 0.50) )
          {
            rgb[0] = 0;
            rgb[1] = 255;    
            rgb[2] = Math.floor(255 * (0.50 - intensity) * 4);
          }
          if ( (0.50 < intensity) && (intensity <= 0.75) )
          {
            rgb[0] = Math.floor(255 * (intensity - 0.50) * 4);
            rgb[1] = 255;    
            rgb[2] = 0;
          }
          if ( (0.75 < intensity) && (intensity <= 1.0) )
          {
            rgb[0] = 255;
            rgb[1] = Math.floor(255 * (1.0 - intensity) * 4);    
            rgb[2] = 0;
          }
          return rgb;
        }
        function clearImage()
        {
            var canvas = document.getElementById("flirCanvas");
